  array('muscle_translation_id' => '59','muscle_translation_muscle_id' => '26','muscle_translation_language' => 'no','muscle_translation_simple_name' => 'Soleus','muscle_translation_short_name' => 'Soleus','muscle_translation_text' => '<p><strong><span id="result_box" class="" lang="no" tabindex="-1">Hva er Soleus?</span></strong></p>
